site.headStyle:
body .editable-style.column-section.container-element {
    padding: 0;
    margin: 0;
}
.adelphe, #adelphe:hover {
    font-family: 'adelphe';
}
@font-face {
    font-family: 'adelphe';
    src: url(../fontes/AdelpheVF.woff2) format('woff2'),
         url(../fontes/AdelpheVF.woff) format('woff');
}

.baskervvol, #baskervvol:hover {
    font-family: 'baskervvol';
}
@font-face {
    font-family: 'baskervvol';
    src: url(../fontes/BBBBaskervvol-Fondue.woff2) format('woff2');
    src: url(../fontes/BBBBaskervvol-Fondue.woff) format('woff');
}




@font-face {
    font-family: 'BBBBaskervvol';
    src: local ('fonts/BBBBaskervvol-Base.woff') format ('woff'),
    	 local ('fonts/BBBBaskervvol-Base.woff2') format ('woff2');
     font-weight: 700;
    font-style: normal;
}

@font-face {
    font-family: 'BBBBaskervol fondue';
    src: local ('fonts/BBBBaskervvol-Fondue.woff') format ('fondu woff'),
         local ('fonts/BBBBaskervvol-Fondue.woff2') format ('fondu woff2');
    font-weight: 700;
    font-style: normal;
}


/* section takes all width */
div.column-section {
  width: 100%;
}
div.column-section > a {
  width: 100%;
}
@media (min-width:481px) {
  .silex-editor div.column-section {
    /* leave space to drop, only in the editor */
    padding: 50px 0;
  }
  div.column-section > a {
    /* 3 elements per line */
    width: 33.333%;
  }
}
